"use strict";

// Initialize when document is ready
$(document).ready(function() {
  
  // Toggle chat message sidebar
  $(".chat-message-sidebar-toggle").click(function() {
    $(".chat-calling-text-message-sidebar").toggleClass("active");
  });
  
  // Call handling functionality
  $(".timetracker").timetracker();
  
  // Handle received call
  $(".call-received").click(function() {
    $(".call-received").hide();
    $(".call-ended").show();
    $(".timetracker").addClass("active");
    $(".ringing").removeClass("active");
  });
  
  // Handle ended call
  $(".call-ended").click(function() {
    $(".call-ended").hide();
    $(".call-received").show();
    $(".timetracker").removeClass("active");
    $(".ringing").addClass("active");
  });
  
  // Initialize emoji picker
  $(".emoji-picker").emojioneArea();
  
});