"use strict";

$(document).ready(function() {
  // File storage search functionality
  $("#storageSearch").on("keyup", function() {
    const searchTerm = $(this).val().toLowerCase();
    
    $(".content-area-body .card").each(function() {
      const cardText = $(this).text().toLowerCase();
      $(this).toggle(cardText.includes(searchTerm));
    });
  });

  // File/folder delete confirmation dialog
  $('[data-action-target="#fileFolderDeleteAction"]').on("click", function(e) {
    e.preventDefault();
    
    const swal = Swal.mixin({
      customClass: {
        confirmButton: "btn btn-success m-1",
        cancelButton: "btn btn-danger m-1"
      },
      buttonsStyling: false
    });
    
    swal.fire({
      title: "Are you sure?",
      text: "You want to delete this file or folder!",
      icon: "warning",
      showCancelButton: true,
      confirmButtonText: "Yes, delete it!",
      cancelButtonText: "No, cancel!",
      reverseButtons: true
    }).then((result) => {
      if (result.value) {
        swal.fire(
          "Deleted!",
          "File or Folder Deleted Successfully.",
          "success"
        );
      } else if (result.dismiss === Swal.DismissReason.cancel) {
        swal.fire(
          "Cancelled",
          "Your file is safe :)",
          "error"
        );
      }
    });
  });
});