"use strict";

$(document).ready(function() {
  // Invoice Send Confirmation Dialog
  $('[data-alert-target="invoicSendMessage"]').on("click", function(event) {
    event.preventDefault();
    
    const swal = Swal.mixin({
      customClass: {
        confirmButton: "btn btn-success m-1",
        cancelButton: "btn btn-danger m-1"
      },
      buttonsStyling: false
    });
    
    swal.fire({
      title: "Are you sure?",
      text: "You want to send this Invoice!",
      icon: "warning",
      showCancelButton: true,
      confirmButtonText: "Yes, send it!",
      cancelButtonText: "No, cancel!",
      reverseButtons: true,
      focusCancel: true,  // Better UX for destructive actions
      allowOutsideClick: false  // Prevent accidental dismissal
    }).then((result) => {
      if (result.isConfirmed) {
        swal.fire({
          title: "Sent!",
          text: "Invoice sent successfully.",
          icon: "success",
          timer: 2000,
          showConfirmButton: false
        }).then(() => {
          // Add your invoice sending logic here
          console.log("Invoice sending confirmed");
        });
      } else if (result.dismiss === Swal.DismissReason.cancel) {
        swal.fire({
          title: "Cancelled",
          text: "Your invoice was not sent.",
          icon: "info",
          timer: 2000,
          showConfirmButton: false
        });
      }
    });
  });

  // Print Invoice Functionality
  $(".printBTN").on("click", function() {
    $(".invoice-container").print({
      globalStyles: true,
      iframe: false,
      // Additional print options can be added here
      mediaPrint: false,
      stylesheet: null,
      noPrintSelector: ".no-print",
      append: null,
      prepend: null
    });
  });
});